import React, {useState} from 'react'

export default function TextForm(props) {
    const handleUpClick = () =>{
        console.log("Uppercase was clicked" + text);
        let newText = text.toUpperCase();
        setText(newText);
    }
    const handleOnChange = (event) =>{
        console.log("Onchange");
        setText(event.target.value);
    }
    const [text, setText] = useState('');
    // text ="new text"; // wrong way to change the state
    // setText("new text"); 
  return (
    <div>
        <h1>{props.heading}</h1>
        <textarea className="form-control" value={text} onChange={handleOnChange} id="myBox" placeholder='Enter text here' rows="8"></textarea> //onchange event 
        <button className='btn btn-success mt-3' onClick={handleUpClick}>Convert to Uppercase</button>
    </div>
  )
}
