import React, {useState} from 'react'

export default function TextForm(props) {
    const handleUpClick = () =>{
        // console.log("Uppercase was clicked" + text);
        let newText = text.toUpperCase();
        setText(newText);
    }
    const handleLoClick = () =>{
        // console.log("Uppercase was clicked" + text);
        let newText = text.toLowerCase();
        setText(newText);
    }
    const handleLloClick = () =>{
        // console.log("Uppercase was clicked" + text);
        let newText = text.char;
        setText(newText);
    }
    const handleOnChange = (event) =>{
        // console.log("Onchange");
        setText(event.target.value);
    }
    const [text, setText] = useState('');
    // text ="new text"; // wrong way to change the state
    // setText("new text"); 
  return (
    <>
        <div className='container'>
            <h1>{props.heading}</h1>
            <textarea className="form-control" value={text} onChange={handleOnChange} id="myBox" placeholder='Enter text here' rows="8"></textarea> {/* onchange event is neccessary for typing new text */}
            <button className='btn btn-success mt-3 me-3' onClick={handleUpClick}>Convert to Uppercase</button>
            <button className='btn btn-success mt-3  me-3' onClick={handleLoClick}>Convert to Lowercase</button>
            <button className='btn btn-success mt-3' onClick={handleLloClick}>Convert to Local</button>
        </div>
        <div className='container my-3'>
            <h1>Your text summary</h1>
            <p>{text.split(" ").length} words, {text.length} characters</p>
            <p>{0.008 * text.split(" ").length} Minutes read</p>
            <h2>Preview</h2>
            <p>{text}</p>
        </div>
    </>
  )
}
